{
<span style="color:#8b008b;font-weight:bold">public</span>:
    vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; p;
    <span style="color:#00688b;font-weight:bold">int</span> pos, max_right;
    string <span style="color:#008b45">init</span>(string s)
    {
        string ans = <span style="color:#cd5555">&#34;#&#34;</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">char</span> ch : s)
            ans = ans + ch + <span style="color:#cd5555">&#39;#&#39;</span>;
        <span style="color:#8b008b;font-weight:bold">return</span> ans;
    }
    string <span style="color:#008b45">longestPalindrome</span>(string s)
    {
        s = init(s);
        p.resize(s.size());
        pos = <span style="color:#b452cd">0</span>, max_right = <span style="color:#b452cd">0</span>;
        <span style="color:#00688b;font-weight:bold">int</span> max_p = <span style="color:#b452cd">0</span>, max_ppos = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; s.size(); i++)
        {
            <span style="color:#8b008b;font-weight:bold">if</span> (i &lt; max_right)
                p[i] = min(p[<span style="color:#b452cd">2</span> * pos - i], max_right - i);
            <span style="color:#8b008b;font-weight:bold">else</span>
                p[i] = <span style="color:#b452cd">1</span>;
            <span style="color:#8b008b;font-weight:bold">while</span> (i - p[i] &gt;= <span style="color:#b452cd">0</span> &amp;&amp; i + p[i] &lt; s.size() &amp;&amp; s[i - p[i]] == s[i + p[i]])
                p[i]++;
            <span style="color:#8b008b;font-weight:bold">if</span> (p[i] + i - <span style="color:#b452cd">1</span> &gt; max_right)
            {
                max_right = p[i] + i - <span style="color:#b452cd">1</span>;
                pos = i;
            }
            <span style="color:#8b008b;font-weight:bold">if</span> (p[i] &gt; max_p)
            {
                max_p = p[i];
                max_ppos = i;
            }
        }
        string ans = <span style="color:#cd5555">&#34;&#34;</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = max_ppos - max_p + <span style="color:#b452cd">1</span>; i &lt;= max_ppos + max_p - <span style="color:#b452cd">1</span>; i++)
            <span style="color:#8b008b;font-weight:bold">if</span> (s[i] != <span style="color:#cd5555">&#39;#&#39;</span>)
                ans += s[i];
        <span style="color:#8b008b;font-weight:bold">return</span> ans;
    }
};
