<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#8b008b;font-weight:bold">struct</span> <span style="color:#008b45;font-weight:bold">node</span>
    {
        <span style="color:#00688b;font-weight:bold">int</span> ch[<span style="color:#b452cd">26</span>];
        <span style="color:#00688b;font-weight:bold">int</span> flag;
        node()
        {
            flag = -<span style="color:#b452cd">1</span>;
            memset(ch, <span style="color:#b452cd">0</span>, <span style="color:#8b008b;font-weight:bold">sizeof</span>(ch));
        }
    };

    vector&lt;node&gt; tree;

    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">insert</span>(string &amp;s, <span style="color:#00688b;font-weight:bold">int</span> id)
    {
        <span style="color:#00688b;font-weight:bold">int</span> len = s.length(), add = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; len; i++)
        {
            <span style="color:#00688b;font-weight:bold">int</span> x = s[i] - <span style="color:#cd5555">&#39;a&#39;</span>;
            <span style="color:#8b008b;font-weight:bold">if</span> (!tree[add].ch[x])
            {
                tree.push_back(node());
                tree[add].ch[x] = tree.size() - <span style="color:#b452cd">1</span>;
            }
            add = tree[add].ch[x];
        }
        tree[add].flag = id;
    }

    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">findWord</span>(string &amp;s, <span style="color:#00688b;font-weight:bold">int</span> left, <span style="color:#00688b;font-weight:bold">int</span> right)
    {
        <span style="color:#00688b;font-weight:bold">int</span> add = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = right; i &gt;= left; i--)
        {
            <span style="color:#00688b;font-weight:bold">int</span> x = s[i] - <span style="color:#cd5555">&#39;a&#39;</span>;
            <span style="color:#8b008b;font-weight:bold">if</span> (!tree[add].ch[x])
            {
                <span style="color:#8b008b;font-weight:bold">return</span> -<span style="color:#b452cd">1</span>;
            }
            add = tree[add].ch[x];
        }
        <span style="color:#8b008b;font-weight:bold">return</span> tree[add].flag;
    }

    <span style="color:#00688b;font-weight:bold">bool</span> <span style="color:#008b45">isPalindrome</span>(string &amp;s, <span style="color:#00688b;font-weight:bold">int</span> l, <span style="color:#00688b;font-weight:bold">int</span> r)
    {
        <span style="color:#8b008b;font-weight:bold">while</span> (l &lt; r)
            <span style="color:#8b008b;font-weight:bold">if</span> (s[l++] != s[r--])
                <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#658b00">false</span>;
        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#658b00">true</span>;
    }

    vector&lt;vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt;&gt; palindromePairs(vector&lt;string&gt; &amp;words)
    {
        tree.push_back(node());
        <span style="color:#00688b;font-weight:bold">int</span> n = words.size();
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; n; i++)
            insert(words[i], i);
        vector&lt;vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt;&gt; ret;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; n; i++)
        {
            <span style="color:#00688b;font-weight:bold">int</span> m = words[i].size();
            <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> j = <span style="color:#b452cd">0</span>; j &lt;= m; j++)
            {
                <span style="color:#8b008b;font-weight:bold">if</span> (isPalindrome(words[i], j, m - <span style="color:#b452cd">1</span>))
                {
                    <span style="color:#00688b;font-weight:bold">int</span> left_id = findWord(words[i], <span style="color:#b452cd">0</span>, j - <span style="color:#b452cd">1</span>);
                    <span style="color:#8b008b;font-weight:bold">if</span> (left_id != -<span style="color:#b452cd">1</span> &amp;&amp; left_id != i)
                        ret.push_back({i, left_id});
                }
                <span style="color:#8b008b;font-weight:bold">if</span> (j &amp;&amp; isPalindrome(words[i], <span style="color:#b452cd">0</span>, j - <span style="color:#b452cd">1</span>))
