<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">Solution</span>
{
<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#8b008b;font-weight:bold">struct</span> <span style="color:#008b45;font-weight:bold">point</span>
    {
        <span style="color:#00688b;font-weight:bold">int</span> row, pos, v;
    };
    <span style="color:#8b008b;font-weight:bold">friend</span> <span style="color:#00688b;font-weight:bold">bool</span> <span style="color:#8b008b;font-weight:bold">operator</span>&lt;(point a, point b)
    {
        <span style="color:#8b008b;font-weight:bold">return</span> a.v &gt; b.v;
    }
    vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; smallestRange(vector&lt;vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt;&gt; &amp;nums)
    {
        <span style="color:#00688b;font-weight:bold">int</span> n = nums.size();
        priority_queue&lt;point&gt; q;
        <span style="color:#00688b;font-weight:bold">int</span> minn = <span style="color:#b452cd">0</span>, maxx = INT_MIN;
        <span style="color:#00688b;font-weight:bold">int</span> l = <span style="color:#b452cd">0</span>, r = INT_MAX;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; n; i++)
        {
            q.push(point{i, <span style="color:#b452cd">0</span>, nums[i][<span style="color:#b452cd">0</span>]});
            maxx = max(maxx, nums[i][<span style="color:#b452cd">0</span>]);
        }
        <span style="color:#8b008b;font-weight:bold">while</span> (!q.empty())
        {
            point p = q.top();
            q.pop();
            minn = p.v;
            <span style="color:#8b008b;font-weight:bold">if</span> (maxx - minn &lt; r - l)
            {
                l = minn;
                r = maxx;
            }
            <span style="color:#8b008b;font-weight:bold">if</span> (p.pos == nums[p.row].size() - <span style="color:#b452cd">1</span>)
                <span style="color:#8b008b;font-weight:bold">break</span>;
            maxx = max(maxx, nums[p.row][p.pos + <span style="color:#b452cd">1</span>]);
            q.push(point{p.row, p.pos + <span style="color:#b452cd">1</span>, nums[p.row][p.pos + <span style="color:#b452cd">1</span>]});
        }
        <span style="color:#8b008b;font-weight:bold">return</span> {l, r};
    }
};
