<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">Solution</span>
{
<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#8b008b;font-weight:bold">struct</span> <span style="color:#008b45;font-weight:bold">status</span>
    {
        ListNode *ptr;
        <span style="color:#00688b;font-weight:bold">int</span> val;
        <span style="color:#00688b;font-weight:bold">bool</span> <span style="color:#8b008b;font-weight:bold">operator</span>&lt;(<span style="color:#8b008b;font-weight:bold">const</span> status &amp;rhs) <span style="color:#8b008b;font-weight:bold">const</span>
        {
            <span style="color:#8b008b;font-weight:bold">return</span> val &gt; rhs.val;
        }
    };
    ListNode *<span style="color:#008b45">mergeKLists</span>(vector&lt;ListNode *&gt; &amp;lists)
    {
        ListNode *head = <span style="color:#658b00">NULL</span>, *last;
        priority_queue&lt;status&gt; q;
        <span style="color:#8b008b;font-weight:bold">for</span> (ListNode *&amp;node : lists)
            <span style="color:#8b008b;font-weight:bold">if</span> (node)
                q.push({node, node-&gt;val});
        <span style="color:#8b008b;font-weight:bold">while</span> (!q.empty())
        {
            status cur = q.top();
            q.pop();
            <span style="color:#8b008b;font-weight:bold">if</span> (head == <span style="color:#658b00">NULL</span>)
            {
                head = cur.ptr;
                last = head;
            }
            <span style="color:#8b008b;font-weight:bold">else</span>
            {
                last-&gt;next = cur.ptr;
                last = last-&gt;next;
            }
            <span style="color:#8b008b;font-weight:bold">if</span> (cur.ptr-&gt;next)
                q.push({cur.ptr-&gt;next, cur.ptr-&gt;next-&gt;val});
        }
        <span style="color:#8b008b;font-weight:bold">return</span> head;
    }
};
