<td style="vertical-align:top;padding:0;margin:0;border:0;;width:100%">
<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">Solution</span>
{
<span style="color:#8b008b;font-weight:bold">public</span>:
    ListNode *mergeKLists(vector&lt;ListNode *&gt; &amp;lists)
    {
        ListNode *head = <span style="color:#658b00">NULL</span>;
        <span style="color:#8b008b;font-weight:bold">while</span> (<span style="color:#b452cd">1</span>)
        {
            <span style="color:#00688b;font-weight:bold">int</span> cnt = <span style="color:#b452cd">0</span>;
            <span style="color:#8b008b;font-weight:bold">for</span> (ListNode *&amp;node : lists)
            {
                <span style="color:#8b008b;font-weight:bold">if</span> (node == <span style="color:#658b00">NULL</span>)
                    cnt++;
                <span style="color:#8b008b;font-weight:bold">else</span>
                {
                    <span style="color:#8b008b;font-weight:bold">if</span> (head == <span style="color:#658b00">NULL</span>)
                    {
                        head = <span style="color:#8b008b;font-weight:bold">new</span> ListNode(node-&gt;val);
                    }
                    <span style="color:#8b008b;font-weight:bold">else</span>
                    {
                        ListNode *tmpHead = head;
                        <span style="color:#8b008b;font-weight:bold">if</span> (node-&gt;val &lt; head-&gt;val)
                        {
                            head = <span style="color:#8b008b;font-weight:bold">new</span> ListNode(node-&gt;val);
                            head-&gt;next = tmpHead;
                        }
                        <span style="color:#8b008b;font-weight:bold">else</span>
                        {
                            <span style="color:#8b008b;font-weight:bold">while</span> (node-&gt;val &gt;= tmpHead-&gt;val &amp;&amp; tmpHead-&gt;next &amp;&amp; node-&gt;val &gt;= tmpHead-&gt;next-&gt;val)
                            {
                                tmpHead = tmpHead-&gt;next;
                            }
                            <span style="color:#8b008b;font-weight:bold">if</span> (tmpHead-&gt;next)
                            {
                                ListNode *tmp = tmpHead-&gt;next;
                                tmpHead-&gt;next = <span style="color:#8b008b;font-weight:bold">new</span> ListNode(node-&gt;val);
                                tmpHead-&gt;next-&gt;next = tmp;
                            }
                            <span style="color:#8b008b;font-weight:bold">else</span>
                            {
                                tmpHead-&gt;next = <span style="color:#8b008b;font-weight:bold">new</span> ListNode(node-&gt;val);
                            }
                        }
                    }
                    node = node-&gt;next;
                }
            }
            <span style="color:#8b008b;font-weight:bold">if</span> (cnt == lists.size())
                <span style="color:#8b008b;font-weight:bold">break</span>;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> head;
    }
};
