{
<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#00688b;font-weight:bold">int</span> sum[<span style="color:#b452cd">4</span> * <span style="color:#b452cd">50001</span>];
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">pushup</span>(<span style="color:#00688b;font-weight:bold">int</span> rt)
    {
        sum[rt] = sum[rt &lt;&lt; <span style="color:#b452cd">1</span>] + sum[rt &lt;&lt; <span style="color:#b452cd">1</span> | <span style="color:#b452cd">1</span>];
    }
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">build</span>(<span style="color:#00688b;font-weight:bold">int</span> l, <span style="color:#00688b;font-weight:bold">int</span> r, <span style="color:#00688b;font-weight:bold">int</span> rt)
    {
        sum[rt] = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">if</span> (l == r)
            <span style="color:#8b008b;font-weight:bold">return</span>;
        <span style="color:#00688b;font-weight:bold">int</span> m = (l + r) &gt;&gt; <span style="color:#b452cd">1</span>;
        build(l, m, rt &lt;&lt; <span style="color:#b452cd">1</span>);
        build(m + <span style="color:#b452cd">1</span>, r, rt &lt;&lt; <span style="color:#b452cd">1</span> | <span style="color:#b452cd">1</span>);
    }
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">update</span>(<span style="color:#00688b;font-weight:bold">int</span> p, <span style="color:#00688b;font-weight:bold">int</span> l, <span style="color:#00688b;font-weight:bold">int</span> r, <span style="color:#00688b;font-weight:bold">int</span> rt)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (l == r)
        {
            sum[rt]++;
            <span style="color:#8b008b;font-weight:bold">return</span>;
        }
        <span style="color:#00688b;font-weight:bold">int</span> m = (l + r) &gt;&gt; <span style="color:#b452cd">1</span>;
        <span style="color:#8b008b;font-weight:bold">if</span> (p &lt;= m)
            update(p, l, m, rt &lt;&lt; <span style="color:#b452cd">1</span>);
        <span style="color:#8b008b;font-weight:bold">else</span>
            update(p, m + <span style="color:#b452cd">1</span>, r, rt &lt;&lt; <span style="color:#b452cd">1</span> | <span style="color:#b452cd">1</span>);
        pushup(rt);
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">query</span>(<span style="color:#00688b;font-weight:bold">int</span> L, <span style="color:#00688b;font-weight:bold">int</span> R, <span style="color:#00688b;font-weight:bold">int</span> l, <span style="color:#00688b;font-weight:bold">int</span> r, <span style="color:#00688b;font-weight:bold">int</span> rt)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (L &lt;= l &amp;&amp; r &lt;= R)
        {
            <span style="color:#8b008b;font-weight:bold">return</span> sum[rt];
        }
        <span style="color:#00688b;font-weight:bold">int</span> m = (l + r) &gt;&gt; <span style="color:#b452cd">1</span>;
        <span style="color:#00688b;font-weight:bold">int</span> ret = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">if</span> (L &lt;= m)
            ret += query(L, R, l, m, rt &lt;&lt; <span style="color:#b452cd">1</span>);
        <span style="color:#8b008b;font-weight:bold">if</span> (R &gt; m)
            ret += query(L, R, m + <span style="color:#b452cd">1</span>, r, rt &lt;&lt; <span style="color:#b452cd">1</span> | <span style="color:#b452cd">1</span>);
        <span style="color:#8b008b;font-weight:bold">return</span> ret;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">reversePairs</span>(vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; &amp;nums)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (nums.size() == <span style="color:#b452cd">0</span>)
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
        vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; temp(nums);
        sort(temp.begin(), temp.end());
        temp.erase(unique(temp.begin(), temp.end()), temp.end());
        build(<span style="color:#b452cd">1</span>, temp.size(), <span style="color:#b452cd">1</span>);
        <span style="color:#00688b;font-weight:bold">int</span> cnt = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; nums.size(); i++)
        {
            <span style="color:#00688b;font-weight:bold">int</span> l = lower_bound(temp.begin(), temp.end(), nums[i]) - temp.begin() + <span style="color:#b452cd">1</span>;
            <span style="color:#00688b;font-weight:bold">int</span> r = temp.size();
            cnt += query(l + <span style="color:#b452cd">1</span>, r, <span style="color:#b452cd">1</span>, r, <span style="color:#b452cd">1</span>);
            update(l, <span style="color:#b452cd">1</span>, r, <span style="color:#b452cd">1</span>);
        }
        <span style="color:#8b008b;font-weight:bold">return</span> cnt;
    }
};
