</span><span style="margin-right:0.4em;padding:0 0.4em 0 0.4em;color:#7f7f7f">47
</span><span style="margin-right:0.4em;padding:0 0.4em 0 0.4em;color:#7f7f7f">48
</span><span style="margin-right:0.4em;padding:0 0.4em 0 0.4em;color:#7f7f7f">49
</span></code></pre></td>
<td style="vertical-align:top;padding:0;margin:0;border:0;;width:100%">
<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">Solution</span>
{
<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#00688b;font-weight:bold">int</span> c[<span style="color:#b452cd">50000</span> + <span style="color:#b452cd">1</span>], N;
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">init</span>(<span style="color:#00688b;font-weight:bold">int</span> n)
    {
        memset(c, <span style="color:#b452cd">0</span>, <span style="color:#8b008b;font-weight:bold">sizeof</span>(<span style="color:#b452cd">0</span>));
        N = n;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">lowbit</span>(<span style="color:#00688b;font-weight:bold">int</span> x)
    {
        <span style="color:#8b008b;font-weight:bold">return</span> x &amp; -x;
    }
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">add</span>(<span style="color:#00688b;font-weight:bold">int</span> i, <span style="color:#00688b;font-weight:bold">int</span> k)
    {
        <span style="color:#8b008b;font-weight:bold">while</span> (i &lt;= N)
        {
            c[i] += k;
            i += lowbit(i);
        }
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">sum</span>(<span style="color:#00688b;font-weight:bold">int</span> n)
    {
        <span style="color:#00688b;font-weight:bold">int</span> sum = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">while</span> (n &gt; <span style="color:#b452cd">0</span>)
        {
            sum += c[n];
            n -= lowbit(n);
        }
        <span style="color:#8b008b;font-weight:bold">return</span> sum;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">reversePairs</span>(vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; &amp;nums)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (nums.size() == <span style="color:#b452cd">0</span>)
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
        vector&lt;<span style="color:#00688b;font-weight:bold">int</span>&gt; temp(nums);
        sort(temp.begin(), temp.end());
        temp.erase(unique(temp.begin(), temp.end()), temp.end());
        init(temp.size());
        <span style="color:#00688b;font-weight:bold">int</span> cnt = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; nums.size(); i++)
        {
            <span style="color:#00688b;font-weight:bold">int</span> x = lower_bound(temp.begin(), temp.end(), nums[i]) - temp.begin() + <span style="color:#b452cd">1</span>;
            add(x, <span style="color:#b452cd">1</span>);
            cnt += i + <span style="color:#b452cd">1</span> - sum(x);
        }
        <span style="color:#8b008b;font-weight:bold">return</span> cnt;
    }
};
