{
<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#00688b;font-weight:bold">bool</span> valid(string &amp;str)
    {
        <span style="color:#00688b;font-weight:bold">int</span> balance = <span style="color:#b452cd">0</span>;
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">char</span> c : str)
        {
            <span style="color:#8b008b;font-weight:bold">if</span> (c == <span style="color:#cd5555">&#39;(&#39;</span>)
                balance++;
            <span style="color:#8b008b;font-weight:bold">else</span>
            {
                balance--;
                <span style="color:#8b008b;font-weight:bold">if</span> (balance &lt; <span style="color:#b452cd">0</span>)
                    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#658b00">false</span>;
            }
        }
        <span style="color:#8b008b;font-weight:bold">return</span> balance == <span style="color:#b452cd">0</span>;
    }
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">generate_all</span>(string &amp;cur, <span style="color:#00688b;font-weight:bold">int</span> n, vector&lt;string&gt; &amp;result)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (n == cur.size())
        {
            <span style="color:#8b008b;font-weight:bold">if</span> (valid(cur))
                result.push_back(cur);
            <span style="color:#8b008b;font-weight:bold">return</span>;
        }
        cur += <span style="color:#cd5555">&#34;(&#34;</span>;
        generate_all(cur, n, result);
        cur.pop_back();
        cur += <span style="color:#cd5555">&#34;)&#34;</span>;
        generate_all(cur, n, result);
        cur.pop_back();
    }
    vector&lt;string&gt; generateParenthesis(<span style="color:#00688b;font-weight:bold">int</span> n)
    {
        vector&lt;string&gt; result;
        string cur = <span style="color:#cd5555">&#34;&#34;</span>;
        generate_all(cur, n * <span style="color:#b452cd">2</span>, result);
        <span style="color:#8b008b;font-weight:bold">return</span> result;
    }
};
