<span style="color:#8b008b;font-weight:bold">public</span>:
    <span style="color:#00688b;font-weight:bold">void</span> generate(string &amp;cur, <span style="color:#00688b;font-weight:bold">int</span> n, <span style="color:#00688b;font-weight:bold">int</span> open, <span style="color:#00688b;font-weight:bold">int</span> close, vector&lt;string&gt; &amp;result)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (cur.size() == n * <span style="color:#b452cd">2</span>)
        {
            result.push_back(cur);
            <span style="color:#8b008b;font-weight:bold">return</span>;
        }
        <span style="color:#8b008b;font-weight:bold">if</span> (open &lt; n)
        {
            cur += <span style="color:#cd5555">&#34;(&#34;</span>;
            generate(cur, n, open + <span style="color:#b452cd">1</span>, close, result);
