<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#8b008b;font-weight:bold">struct</span> <span style="color:#008b45;font-weight:bold">Node</span>
{
    <span style="color:#228b22">// æŸ¥è¯¢æ¬¡æ•°ã€æ—¶é—´æˆ³ã€kã€v
</span><span style="color:#228b22"></span>    <span style="color:#00688b;font-weight:bold">int</span> cnt, time, key, value;

    Node(<span style="color:#00688b;font-weight:bold">int</span> _cnt, <span style="color:#00688b;font-weight:bold">int</span> _time, <span style="color:#00688b;font-weight:bold">int</span> _key, <span style="color:#00688b;font-weight:bold">int</span> _value) : cnt(_cnt), time(_time), key(_key), value(_value) {}

    <span style="color:#00688b;font-weight:bold">bool</span> <span style="color:#8b008b;font-weight:bold">operator</span>&lt;(<span style="color:#8b008b;font-weight:bold">const</span> Node &amp;rhs) <span style="color:#8b008b;font-weight:bold">const</span>
    {
        <span style="color:#8b008b;font-weight:bold">return</span> cnt == rhs.cnt ? time &lt; rhs.time : cnt &lt; rhs.cnt;
    }
};
<span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">LFUCache</span>
{
    <span style="color:#228b22">// ç¼“å­˜å®¹é‡ï¼Œæ—¶é—´æˆ³
</span><span style="color:#228b22"></span>    <span style="color:#00688b;font-weight:bold">int</span> capacity, time;
    unordered_map&lt;<span style="color:#00688b;font-weight:bold">int</span>, Node&gt; key_table;
    set&lt;Node&gt; S;

<span style="color:#8b008b;font-weight:bold">public</span>:
    LFUCache(<span style="color:#00688b;font-weight:bold">int</span> _capacity)
    {
        capacity = _capacity;
        time = <span style="color:#b452cd">0</span>;
        key_table.clear();
        S.clear();
    }

    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">get</span>(<span style="color:#00688b;font-weight:bold">int</span> key)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (capacity == <span style="color:#b452cd">0</span>)
            <span style="color:#8b008b;font-weight:bold">return</span> -<span style="color:#b452cd">1</span>;
        <span style="color:#8b008b;font-weight:bold">auto</span> it = key_table.find(key);
        <span style="color:#228b22">// å¦‚æœå“ˆå¸Œè¡¨ä¸­æ²¡æœ‰é”® keyï¼Œè¿”å› -1
</span><span style="color:#228b22"></span>        <span style="color:#8b008b;font-weight:bold">if</span> (it == key_table.end())
            <span style="color:#8b008b;font-weight:bold">return</span> -<span style="color:#b452cd">1</span>;
        <span style="color:#228b22">// ä»å“ˆå¸Œè¡¨ä¸­å¾—åˆ°æ—§çš„ç¼“å­˜
</span><span style="color:#228b22"></span>        Node cache = it-&gt;second;
        <span style="color:#228b22">// ä»å¹³è¡¡äºŒå‰æ ‘ä¸­åˆ é™¤æ—§çš„ç¼“å­˜
</span><span style="color:#228b22"></span>        S.erase(cache);
        <span style="color:#228b22">// å°†æ—§ç¼“å­˜æ›´æ–°
</span><span style="color:#228b22"></span>        cache.cnt += <span style="color:#b452cd">1</span>;
        cache.time = ++time;
        <span style="color:#228b22">// å°†æ–°ç¼“å­˜é‡æ–°æ”¾å…¥å“ˆå¸Œè¡¨å’Œå¹³è¡¡äºŒå‰æ ‘ä¸­
</span><span style="color:#228b22"></span>        S.insert(cache);
        it-&gt;second = cache;
        <span style="color:#8b008b;font-weight:bold">return</span> cache.value;
    }

    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">put</span>(<span style="color:#00688b;font-weight:bold">int</span> key, <span style="color:#00688b;font-weight:bold">int</span> value)
    {
        <span style="color:#8b008b;font-weight:bold">if</span> (capacity == <span style="color:#b452cd">0</span>)
            <span style="color:#8b008b;font-weight:bold">return</span>;
        <span style="color:#8b008b;font-weight:bold">auto</span> it = key_table.find(key);
        <span style="color:#8b008b;font-weight:bold">if</span> (it == key_table.end())
        {
            <span style="color:#228b22">// å¦‚æœåˆ°è¾¾ç¼“å­˜å®¹é‡ä¸Šé™
</span><span style="color:#228b22"></span>            <span style="color:#8b008b;font-weight:bold">if</span> (key_table.size() == capacity)
            {
                <span style="color:#228b22">// ä»å“ˆå¸Œè¡¨å’Œå¹³è¡¡äºŒå‰æ ‘ä¸­åˆ é™¤æœ€è¿‘æœ€å°‘ä½¿ç”¨çš„ç¼“å­˜
</span><span style="color:#228b22"></span>                key_table.erase(S.begin()-&gt;key);
                S.erase(S.begin());
            }
            <span style="color:#228b22">// åˆ›å»ºæ–°çš„ç¼“å­˜
</span><span style="color:#228b22"></span>            Node cache = Node(<span style="color:#b452cd">1</span>, ++time, key, value);
            <span style="color:#228b22">// å°†æ–°ç¼“å­˜æ”¾å…¥å“ˆå¸Œè¡¨å’Œå¹³è¡¡äºŒå‰æ ‘ä¸­
</span><span style="color:#228b22"></span>            key_table.insert(make_pair(key, cache));
            S.insert(cache);
        }
        <span style="color:#8b008b;font-weight:bold">else</span>
        {
            <span style="color:#228b22">// è¿™é‡Œå’Œ get() å‡½æ•°ç±»ä¼¼
</span><span style="color:#228b22"></span>            Node cache = it-&gt;second;
            S.erase(cache);
            cache.cnt += <span style="color:#b452cd">1</span>;
            cache.time = ++time;
            cache.value = value;
            S.insert(cache);
            it-&gt;second = cache;
        }
    }
};
