<pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;cstdio&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;stack&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b"></span><span style="color:#8b008b;font-weight:bold">using</span> <span style="color:#8b008b;font-weight:bold">namespace</span> std;
<span style="color:#8b008b;font-weight:bold">typedef</span> <span style="color:#00688b;font-weight:bold">long</span> <span style="color:#00688b;font-weight:bold">long</span> ll;
<span style="color:#8b008b;font-weight:bold">const</span> ll N = <span style="color:#b452cd">1e6</span> + <span style="color:#b452cd">10</span>;
ll a[N], num[N], l[N], r[N];
<span style="color:#8b008b;font-weight:bold">struct</span> <span style="color:#008b45;font-weight:bold">node</span>
{
    ll x, id;
};
<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>()
{
    <span style="color:#228b22">//freopen(&#34;in.txt&#34;, &#34;r&#34;, stdin);
</span><span style="color:#228b22"></span>    ll n;
    <span style="color:#8b008b;font-weight:bold">while</span> (~scanf(<span style="color:#cd5555">&#34;%lld&#34;</span>, &amp;n))
    {
        stack&lt;node&gt; s;
        <span style="color:#8b008b;font-weight:bold">for</span> (ll i = <span style="color:#b452cd">1</span>; i &lt;= n; i++)
        {
            scanf(<span style="color:#cd5555">&#34;%lld&#34;</span>, &amp;a[i]);
            num[i] = num[i - <span style="color:#b452cd">1</span>] + a[i];
        }
        <span style="color:#8b008b;font-weight:bold">for</span> (ll i = <span style="color:#b452cd">1</span>; i &lt;= n; i++)
        {
            <span style="color:#8b008b;font-weight:bold">while</span> (!s.empty() &amp;&amp; s.top().x &gt; a[i])
            {
                ll id = s.top().id;
                s.pop();
                r[id] = i - <span style="color:#b452cd">1</span>;
            }
            s.push({a[i], i});
        }
        <span style="color:#8b008b;font-weight:bold">while</span> (!s.empty())
        {
            ll id = s.top().id;
            s.pop();
            r[id] = n;
        }
        <span style="color:#8b008b;font-weight:bold">for</span> (ll i = n; i &gt;= <span style="color:#b452cd">1</span>; i--)
        {
            <span style="color:#8b008b;font-weight:bold">while</span> (!s.empty() &amp;&amp; s.top().x &gt; a[i])
            {
                ll id = s.top().id;
                s.pop();
                l[id] = i + <span style="color:#b452cd">1</span>;
            }
            s.push({a[i], i});
        }
        <span style="color:#8b008b;font-weight:bold">while</span> (!s.empty())
        {
            ll id = s.top().id;
            s.pop();
            l[id] = <span style="color:#b452cd">1</span>;
        }
        ll maxx = -<span style="color:#b452cd">1</span>, ansl, ansr;
        <span style="color:#8b008b;font-weight:bold">for</span> (ll i = <span style="color:#b452cd">1</span>; i &lt;= n; i++)
        {
            ll sum = a[i] * (num[r[i]] - num[l[i] - <span style="color:#b452cd">1</span>]);
            <span style="color:#8b008b;font-weight:bold">if</span> (sum &gt; maxx)
            {
                maxx = sum;
                ansl = l[i];
                ansr = r[i];
            }
        }
        printf(<span style="color:#cd5555">&#34;%lld</span><span style="color:#cd5555">\n</span><span style="color:#cd5555">%lld %lld</span><span style="color:#cd5555">\n</span><span style="color:#cd5555">&#34;</span>, maxx, ansl, ansr);
    }
    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
}
